mapper.insert:

INSERT INTO tbl_cart(userId, product_id, amount) VALUES (
	#{userId}, #{product_id}, #{amount}
)

mapper.select:
SELECT IFNULL(SUM(product_price * amount), 0) money
FROM tbl_cart c, tbl_product p
WHERE c.product_id = p.product_id AND c.userId=#{userId}

mapper.update:
UPDATE tbl_cart SET amount=#{amount}
WHERE userId = #{userId}
AND product_id=#{product_id}

mapper.select:
SELECT COUNT(*) FROM tbl_cart
WHERE userId=#{userId}
AND product_id=#{product_id}

mapper.update:
UPDATE tbl_cart SET amount=#{amount}
WHERE userId=#{userId}
AND product_id=#{product_id}
